      { id: 2,label: '[4] Designer', posX: 210, posY: 0 },
      { id: 3,label: '[5] Scroll Down For More', posX: 675, posY: 0 },
    ])

let dragging = false
let startX = 0
let startY = 0
let currentIndex = null
let currentList = null

const getClientPos = (e) => {
  if (e.touches && e.touches.length > 0) {
    return {
      x: e.touches[0].clientX,
      y: e.touches[0].clientY
    }
  }
  return { x: e.clientX, y: e.clientY }
}
const onDrag = (e: MouseEvent) => {
  if (!dragging) return

  e.preventDefault() // 重要：避免手機滑動整個畫面

  const item = currentList === 'top' ? topItems[currentIndex] : botItems[currentIndex]
  const { x, y } = getClientPos(e)

  item.posX = x - startX
  item.posY = y - startY
}

const startDrag = (e, index, list) => {
  dragging = true
  currentIndex = index
  currentList = list

  const item = list === 'top' ? topItems[index] : botItems[index]

  const { x, y } = getClientPos(e)

  startX = x - item.posX
  startY = y - item.posY

  // 電腦
  window.addEventListener('mousemove', onDrag)
  window.addEventListener('mouseup', stopDrag)

  // 手機
  window.addEventListener('touchmove', onDrag, { passive: false })
  window.addEventListener('touchend', stopDrag)
}

const stopDrag = () => {
  dragging = false

  // 電腦
  window.removeEventListener('mousemove', onDrag)
  window.removeEventListener('mouseup', stopDrag)

  // 手機
  window.removeEventListener('touchmove', onDrag)
  window.removeEventListener('touchend', stopDrag)
}

