onMounted(() => {
  if (window.innerWidth <= 800) {
  const container = p31.value;
  const items = container.querySelectorAll('.item');
  const activeItem = ref(0);
  const currentPicIndex = ref({});
  const intervals = {};

const startPicCarousel = (index) => {
  const itemData = itemsData[index];  
  if (!itemData?.pics?.length) return;

  // 取得該 item 的 hpic 容器
  const itemEl = document.querySelectorAll('.item')[index];
  const hpic = itemEl.querySelector('.hpic');
  if (!hpic) return;

  // 如果 slides 已經存在，不要重建，只啟動 interval
  const existingSlides = hpic.querySelectorAll('.slide');
  if (existingSlides.length === 0) {
    itemData.pics.forEach((url, idx) => {
      const slide = document.createElement("div");
      slide.classList.add("slide");
      slide.style.backgroundImage = `url(${url})`;
      slide.style.opacity = '0';
      hpic.appendChild(slide);
    });
  }

  if (!(index in currentPicIndex.value)) currentPicIndex.value[index] = 0;
  if (intervals[index]) return;

  let i = currentPicIndex.value[index];
  const slides = hpic.querySelectorAll<HTMLElement>('.slide');

  slides.forEach((s, idx) => {
    s.style.opacity = idx === i ? '1' : '0';
  });

  intervals[index] = setInterval(() => {
    slides.forEach((s, idx) => {
      s.style.opacity = idx === i ? '1' : '0';
    });
    i = (i + 1) % itemData.pics.length;
    currentPicIndex.value[index] = i;
  }, 2000);
};

const stopPicCarousel = (index: number) => {
  if (intervals[index]) clearInterval(intervals[index]);
  intervals[index] = null;
};


  const checkClosestItem = () => {
    const containerRect = container.getBoundingClientRect();
    let closest = 0;
    let closestOffset = Infinity;

    items.forEach((item: HTMLElement, i: number) => {
      const offsetLeft = Math.abs(item.getBoundingClientRect().left - containerRect.left);
      if (offsetLeft < closestOffset) {
        closestOffset = offsetLeft;
        closest = i;
        }
      });

    if (closest !== activeItem.value) {
      stopPicCarousel(activeItem.value);
      startPicCarousel(closest);
      activeItem.value = closest;
    }
  };
nextTick(() => {
  const container = p31.value;
  if (!container) return;
const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          console.log("➡ p31 出現在畫面，啟動 checkClosestItem()");
          checkClosestItem();
          observer.disconnect();
        }
      });
    }, {
      threshold: 0.3
    });
    observer.observe(container);
    // scroll 時正常更新
    container.addEventListener('scroll', checkClosestItem);
});
}
});

const p5 = ref(null);
const textp5 = "My work is rooted in the quiet details of everyday life, capturing fleeting moments and subtle emotions. Beyond design, I find inspiration in drawing, reading, and observing the world around me, allowing these everyday encounters to quietly shape my creations.";
const sp5 = textp5.split(' ');

const mw1 = ref(null);

let index = 0;
let isActive = false;
let lastTriggerX = 0;
let lastTriggerY = 0;
let accumulatedDistance = 0;
const maxWords = 20;

const container = document.createElement('div');
container.style.position = 'fixed';
container.style.left = '0';
container.style.top = '0';
container.style.pointerEvents = 'none';
container.style.zIndex = '99';
document.body.appendChild(container);

const measureSpan = document.createElement('span');
measureSpan.style.position = 'fixed';
measureSpan.style.visibility = 'hidden';
container.style.width = '0';
container.style.height = '0';
measureSpan.style.whiteSpace = 'pre';
measureSpan.style.font = '17px monospace';
measureSpan.style.transform = "translateY(-50%)";
document.body.appendChild(measureSpan);

function getTextWidth(text: string) {
  measureSpan.textContent = text;
  return measureSpan.offsetWidth;
}

// 當滑鼠進入 p5 區域
function handleMouseEnter(e: MouseEvent) {
  isActive = true;
  index = 0;
  // 初始化觸發點為滑鼠當下位置
  lastTriggerX = e.clientX;
  lastTriggerY = e.clientY;
}

// 離開區域
function handleMouseLeave() {
  isActive = false;
}

// 滑鼠移動檢查
function handlemm(e: MouseEvent) {
  if (!isActive) return;


  // 計算從上次觸發位置移動的距離
const currentWord = sp5[index % sp5.length] || '';
const dx = e.clientX - lastTriggerX;
const dy = e.clientY - lastTriggerY;
const distance = Math.sqrt(dx*dx + dy*dy);

accumulatedDistance += distance;

const currentWordWidth = getTextWidth(currentWord) + 10;
if (accumulatedDistance >= currentWordWidth) {
    showWord(currentWord, e.clientX, e.clientY);
    accumulatedDistance -= currentWordWidth; // 扣掉已觸發的距離
    index++;
}
// 更新觸發點（只影響下一次距離判斷）
lastTriggerX = e.clientX;
lastTriggerY = e.clientY;
}

const showWord = (kk: string, x: number, y: number) => {
  const wordSpan = document.createElement('span');
  wordSpan.classList.add('p5word');

  const width = getTextWidth(kk);
  wordSpan.style.position = 'fixed';
  wordSpan.style.left = `${x}px`;
  wordSpan.style.top = `${y - 20}px`;
  wordSpan.style.width = width + 'px';
  wordSpan.style.display = 'inline-block';
  wordSpan.style.textAlign = 'center';
  wordSpan.style.whiteSpace = 'nowrap';
  wordSpan.style.visibility = 'visible';

  container.appendChild(wordSpan);

if(x > lastTriggerX) {
  wordSpan.style.transform = 'translateX(-100%)'; }

  // 逐字動畫：**不影響寬度**
  const letters = kk.split('');
  letters.forEach((char, i) => {
    const span = document.createElement('span');
    span.textContent = char;
    span.style.display = 'inline-block';
    span.style.opacity = '0';
    span.style.transform = 'translateX(-5px)';
    wordSpan.appendChild(span);

    setTimeout(() => {
      span.style.transition = 'all 0.1s ease';
      span.style.opacity = '1';
      span.style.transform = 'translateX(0)';
    }, i * 70);
  });
if (container.children.length > maxWords) {
  //const firstWord = container.children[0];
  const excessWords = Array.from(container.children).slice(0, container.children.length - maxWords);

  excessWords.forEach(wordSpan => {
    startWordFadeOut(wordSpan as HTMLElement); 
  });
}

function startWordFadeOut(wordSpan: HTMLElement) {
  if (!wordSpan) return;

  const letters = Array.from(wordSpan.children) as Array<HTMLElement>;

  letters.forEach((char, i) => {
    setTimeout(() => {
      char.style.opacity = '0';
    }, i * 50);
  });

  const totalTime = letters.length * 50 + 300;
  setTimeout(() => {
    if (wordSpan && wordSpan.parentNode) {
      wordSpan.parentNode.removeChild(wordSpan);
    }
  }, totalTime);
}
  let isClearing = false;

  window.addEventListener('scroll', () => {
    if (isClearing) return;

    isClearing = true;
    Array.from(container.children).forEach(wordSpan => {
      startWordFadeOut(wordSpan as HTMLElement);
    });

    setTimeout(() => { isClearing = false }, 1000); // 1秒後才允許下一次
  });
}

