const mewRef3 = ref(null);
onMounted(() => {
  if (!mewRef3.value) return;

  const parentWidth = mewRef3.value.clientWidth;
  const originalText = mewRef3.value.innerText;
  mewRef3.value.innerHTML = ' ';

  // 拆成單詞 (保留空格)
  const words = originalText.split(/(\s+)/);
  const spans = [];


  // 建立隱藏測量用 span
  const measureSpan = document.createElement("span");
  measureSpan.style.visibility = "hidden";
  measureSpan.style.whiteSpace = "pre";
  measureSpan.style.display = "inline-block";
  measureSpan.style.letterSpacing = "0.1em";
  mewRef3.value.appendChild(measureSpan);

  let currentLineWidth = 0;

  words.forEach((word: string) => {

  
    if (word === "\n") {
      mewRef3.value.appendChild(document.createElement("br"));
      currentLineWidth = 0;
      return;
    }

    measureSpan.textContent = word === " " ? "\u00A0" : word;
    const wordWidth = measureSpan.offsetWidth;

    if (currentLineWidth + wordWidth > parentWidth) {
      mewRef3.value.appendChild(document.createElement("br"));
      currentLineWidth = 0;
    }

    const span = document.createElement("span");
    span.textContent = word === " " ? "\u00A0" : word;
    span.style.display = "inline-block";
    spans.push(span);
    mewRef3.value.appendChild(span);

    currentLineWidth += wordWidth;
  });
  measureSpan.remove();

    gsap.to(".personal2", {
    filter: "blur(5px)",
    scrollTrigger: {
      trigger: ".personal1",
      start: "bottom 45%",
      end: "bottom -200%",
      toggleActions: "play reverse play reverse",
      scrub: true,
    }
  });

    gsap.from((spans), {
    opacity: 0.3,
    stagger: 0.1,
    scrollTrigger: {
      trigger: ".personal1",
      start: "top 64px",
      end: "bottom top",
      scrub: true,
      pin: true,
      pinType: isWide.value ? "fixed" : "transform",
    }
  });
});

